import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedMenu, setSelectedMenu] = useState('my badges');
  const [selectedImage, setSelectedImage] = useState(null);

  const handleMenuClick = (menu) => {
    setSelectedMenu(menu);
    setSelectedImage(null);
  }

  return (
    <div className="App">
      <div className="menu">
        <button onClick={() => handleMenuClick('my badges')}>my badges</button>
        <button onClick={() => handleMenuClick('my cert')}>my cert</button>
        <button onClick={() => handleMenuClick('my data')}>my data</button>
      </div>

      {selectedImage ? (
        <div className="details">
          <img src={selectedImage} alt="Selected" />
          <h2>Title</h2>
          <p>Experience</p>
          <p>Date</p>
          <p>Name & Email Address</p>
          <button>Update</button>
          <button>Delete</button>
        </div>
      ) : (
        <div className="gallery">
          {/* Display images based on selectedMenu */}
        </div>
      )}
    </div>
  );
}

export default App;
